const highlightPageIcon=function(){const n=window.location.pathname.toLowerCase(),t="selected-icon";n.startsWith("/facts")?$("#icon-home").addClass(t):n.startsWith("/favorites")?$("#icon-favorites").addClass(t):n.startsWith("/bio")?$("#icon-chuck").addClass(t):n.startsWith("/contact")&&$("#icon-contact").addClass(t)};$(document).ready(function(){if(highlightPageIcon(),Notification.permission==="granted"){const n=$("#notification-icon");n.css({"-webkit-filter":"grayscale(0)",filter:"grayscale(0)"})}$("#notification-icon").on("click",function(){const n=$(this);Notification.permission!=="granted"&&Notification.requestPermission().then(()=>{Notification.permission==="granted"?n.css({"-webkit-filter":"grayscale(0)",filter:"grayscale(0)"}):n.css({"-webkit-filter":"grayscale(100%)",filter:"grayscale(100%)"})})})});